import React from 'react'
import { Box, Button, Typography, Paper } from '@mui/material'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'
import logo from '../assets/logoISTLCExtPNG.png'

export default function Home () {
  const navigate = useNavigate()

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #00274D, #041C32)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 3
      }}
    >
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.2 }}
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '2rem',
          flexDirection: 'row'
        }}
      >
        <motion.div
          initial={{ x: -100, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <img
            src={logo}
            alt='Logo ISTLC'
            style={{
              width: '180px',
              height: 'auto',
              filter: 'drop-shadow(0 0 10px rgba(255,255,255,0.5))'
            }}
          />
        </motion.div>

        <motion.div
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          <Paper
            elevation={10}
            sx={{
              p: 5,
              maxWidth: 600,
              borderRadius: '24px',
              backgroundColor: 'rgba(255, 255, 255, 0.08)',
              backdropFilter: 'blur(14px)',
              border: '1px solid rgba(255,255,255,0.15)',
              color: 'white',
              textAlign: 'center',
              boxShadow: '0 6px 40px rgba(0, 0, 0, 0.4)'
            }}
          >
            <Typography
              variant='h3'
              gutterBottom
              sx={{
                fontFamily: 'Segoe UI, sans-serif',
                fontWeight: 800,
                color: '#FFD700',
                mb: 2,
                textShadow: '0 0 10px rgba(255, 215, 0, 0.5)'
              }}
            >
              Bienvenido a AutoEval
            </Typography>

            <Typography
              variant='h6'
              sx={{
                mb: 4,
                color: '#B0C4DE',
                fontStyle: 'italic',
                fontSize: '1.25rem'
              }}
            >
              Plataforma interactiva para reforzar tu aprendizaje con evaluaciones dinámicas y personalizadas.
            </Typography>

            <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
              <Button
                onClick={() => navigate('/register')}
                sx={{
                  backgroundColor: '#FFD700',
                  color: '#000',
                  fontWeight: 'bold',
                  px: 4,
                  py: 1.5,
                  borderRadius: '12px',
                  '&:hover': {
                    backgroundColor: '#FFCC00'
                  }
                }}
              >
                Crear cuenta
              </Button>

              <Button
                onClick={() => navigate('/login')}
                sx={{
                  backgroundColor: '#1E90FF',
                  color: '#fff',
                  fontWeight: 'bold',
                  px: 4,
                  py: 1.5,
                  borderRadius: '12px',
                  '&:hover': {
                    backgroundColor: '#3399FF'
                  }
                }}
              >
                Iniciar sesión
              </Button>
            </Box>
          </Paper>
        </motion.div>
      </motion.div>
    </Box>
  )
}
