import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'

export default function Login () {
  const [formData, setFormData] = useState({ username: '', password: '' })
  const [error, setError] = useState('')
  const navigate = useNavigate()

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value })
  }

  const handleSubmit = async e => {
    e.preventDefault()
    try {
      const res = await axios.post('http://localhost:3001/api/auth/login', formData, { withCredentials: true })
      if (res.data.token) navigate('/preguntas')
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error al iniciar sesión')
    }
  }

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2 style={styles.title}>Iniciar Sesión</h2>
        <input name='username' placeholder='Nombre de usuario' onChange={handleChange} style={styles.input} />
        <input name='password' placeholder='Contraseña' type='password' onChange={handleChange} style={styles.input} />
        <button type='submit' style={{ ...styles.button, backgroundColor: '#1e88e5', color: '#fff' }}>
          ENTRAR
        </button>
        {error && <p style={styles.error}>❌ {error}</p>}
      </form>
    </div>
  )
}

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(to bottom right, #0f2027, #203a43, #2c5364)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '2rem'
  },
  form: {
    background: 'rgba(255, 255, 255, 0.05)',
    backdropFilter: 'blur(12px)',
    padding: '2rem',
    borderRadius: '16px',
    maxWidth: '400px',
    width: '100%',
    color: '#fff',
    boxShadow: '0 8px 20px rgba(0,0,0,0.3)'
  },
  title: {
    marginBottom: '1.5rem',
    textAlign: 'center',
    color: '#1e88e5'
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '1rem',
    border: '1px solid #ccc',
    borderRadius: '10px',
    backgroundColor: 'rgba(255,255,255,0.1)',
    color: '#fff'
  },
  button: {
    fontWeight: 'bold',
    border: 'none',
    padding: '10px',
    width: '100%',
    borderRadius: '10px',
    cursor: 'pointer'
  },
  error: {
    marginTop: '1rem',
    color: '#f55'
  }
}
