import React from 'react'
import { Box, Button, Typography, Paper } from '@mui/material'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'

export default function Home () {
  const navigate = useNavigate()

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #002855, #005BAC)', // azul marino y azul claro
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 3
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: -40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <Paper
          elevation={12}
          sx={{
            p: 5,
            maxWidth: 620,
            borderRadius: '24px',
            backgroundColor: 'rgba(255, 255, 255, 0.06)',
            backdropFilter: 'blur(14px)',
            border: '1px solid rgba(255,255,255,0.2)',
            color: '#fff',
            textAlign: 'center',
            boxShadow: '0 6px 20px rgba(0, 0, 0, 0.35)'
          }}
        >
          <Typography
            variant='h3'
            gutterBottom
            sx={{
              fontFamily: 'Georgia, serif',
              fontWeight: 700,
              color: '#FFD700', // Amarillo del logo
              mb: 2,
              textShadow: '0 0 10px rgba(255, 215, 0, 0.4)'
            }}
          >
            Bienvenido a AutoEval
          </Typography>

          <Typography
            variant='h6'
            sx={{
              mb: 4,
              color: '#E0E0E0',
              fontStyle: 'italic',
              fontSize: '1.2rem'
            }}
          >
            Plataforma de evaluaciÃ³n dinÃ¡mica e inteligente del ISTLC.
            <br />
            Donde el aprendizaje se adapta a ti. âœ¨ðŸ“š
          </Typography>

          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
            <Button
              onClick={() => navigate('/register')}
              sx={{
                backgroundColor: '#FFD700',
                color: '#000',
                fontWeight: 'bold',
                px: 4,
                py: 1.5,
                borderRadius: '12px',
                '&:hover': {
                  backgroundColor: '#FFCC00'
                }
              }}
            >
              Registrarse
            </Button>

            <Button
              onClick={() => navigate('/login')}
              sx={{
                backgroundColor: '#005BAC',
                color: '#fff',
                fontWeight: 'bold',
                px: 4,
                py: 1.5,
                borderRadius: '12px',
                '&:hover': {
                  backgroundColor: '#0072CE'
                }
              }}
            >
              Iniciar sesiÃ³n
            </Button>
          </Box>
        </Paper>
      </motion.div>
    </Box>
  )
}
