import React from 'react'
import { Box, Button, Typography, Paper } from '@mui/material'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'
import logoISTLC from '../assets/logoISTLC.png' 

export default function Home () {
  const navigate = useNavigate()

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'radial-gradient(circle at top, #002855 0%, #000d1a 100%)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 2
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <Paper
          elevation={20}
          sx={{
            p: 6,
            maxWidth: 640,
            borderRadius: '30px',
            background: 'rgba(255, 255, 255, 0.05)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255,255,255,0.2)',
            color: '#fff',
            textAlign: 'center',
            position: 'relative',
            overflow: 'hidden',
            fontFamily: 'Poppins, sans-serif'
          }}
        >
          {/* LOGO flotante */}
          <Box
            component="img"
            src={logoISTLC}
            alt="Logo ISTLC"
            sx={{
              width: 80,
              position: 'absolute',
              top: -40,
              left: '50%',
              transform: 'translateX(-50%)',
              background: '#fff',
              borderRadius: '50%',
              boxShadow: '0 0 20px rgba(255,255,255,0.4)',
              p: 1
            }}
          />

          <Typography
            variant="h3"
            sx={{
              fontWeight: 600,
              color: '#FFD700',
              mb: 2,
              mt: 6
            }}
          >
            AutoEval ISTLC
          </Typography>

          <Typography
            variant="h6"
            sx={{
              mb: 4,
              color: '#ccc',
              fontWeight: 300
            }}
          >
