import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'

export default function Register () {
  const [formData, setFormData] = useState({
    username: '',
    name: '',
    email: '',
    password: ''
  })

  const [error, setError] = useState('')
  const navigate = useNavigate()

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value })
  }

  const handleSubmit = async e => {
    e.preventDefault()
    try {
      await axios.post('http://localhost:3001/api/auth/register', formData)
      navigate('/login')
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error desconocido')
    }
  }

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2 style={styles.title}>Registro de Usuario</h2>
        <input name='username' placeholder='Nombre de usuario' onChange={handleChange} style={styles.input} />
        <input name='name' placeholder='Nombre completo' onChange={handleChange} style={styles.input} />
        <input name='email' placeholder='Correo electrónico' type='email' onChange={handleChange} style={styles.input} />
        <input name='password' placeholder='Contraseña' type='password' onChange={handleChange} style={styles.input} />
        <button type='submit' style={styles.button}>REGISTRARSE</button>
        {error && <p style={styles.error}>❌ {error}</p>}
      </form>
    </div>
  )
}

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(to bottom right, #0f2027, #203a43, #2c5364)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '2rem'
  },
  form: {
    background: 'rgba(255, 255, 255, 0.05)',
    backdropFilter: 'blur(12px)',
    padding: '2rem',
    borderRadius: '16px',
    maxWidth: '400px',
    width: '100%',
    color: '#fff',
    boxShadow: '0 8px 20px rgba(0,0,0,0.3)'
  },
  title: {
    marginBottom: '1.5rem',
    textAlign: 'center',
    color: '#ffd700'
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '1rem',
    border: '1px solid #ccc',
    borderRadius: '10px',
    backgroundColor: 'rgba(255,255,255,0.1)',
    color: '#fff'
  },
  button: {
    backgroundColor: '#ffd700',
    color: '#000',
    fontWeight: 'bold',
    border: 'none',
    padding: '10px',
    width: '100%',
    borderRadius: '10px',
    cursor: 'pointer'
  },
  error: {
    marginTop: '1rem',
    color: '#f55'
  }
}
