import React from 'react'
import { Button, Container, Typography, Box } from '@mui/material'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'

export default function Home () {
  const navigate = useNavigate()

  return (
    <Box
      sx={{
        background: 'linear-gradient(to right, #1c1c1c, #2b2b2b)',
        minHeight: '100vh',
        color: 'white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        px: 3
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <Container maxWidth='sm'>
          <Typography variant='h3' sx={{ fontWeight: 'bold', mb: 2, textShadow: '0px 0px 10px #00f2ff' }}>
            ðŸš€ Bienvenido a AutoEval
          </Typography>

          <Typography variant='h6' sx={{ mb: 4, color: '#ccc' }}>
            Potencia tu mente. Aprende, responde, evoluciona.  
            Tu viaje de conocimiento empieza aquÃ­. ðŸ§ âœ¨
          </Typography>

          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
            <Button
              variant='contained'
              sx={{
                bgcolor: '#00f2ff',
                color: '#000',
                fontWeight: 'bold',
                '&:hover': {
                  bgcolor: '#00d4e0'
                }
              }}
              onClick={() => navigate('/register')}
            >
              Comenzar
            </Button>

            <Button
              variant='outlined'
              sx={{
                color: '#00f2ff',
                borderColor: '#00f2ff',
                fontWeight: 'bold',
                '&:hover': {
                  bgcolor: '#00f2ff22'
                }
              }}
              onClick={() => navigate('/login')}
            >
              Ya tengo cuenta
            </Button>
          </Box>
        </Container>
      </motion.div>
    </Box>
  )
}
