import React, { useState } from 'react'
import { TextField, Button, Typography, Container, Box } from '@mui/material'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

export default function Register () {
  const [formData, setFormData] = useState({
    username: '',
    name: '',
    email: '',
    password: ''
  })

  const [mensaje, setMensaje] = useState('')
  const navigate = useNavigate()

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      const response = await axios.post(
        'http://localhost:3001/api/auth/register',
        formData
      )

      setMensaje('‚úÖ Registro exitoso, redirigiendo...')
      setTimeout(() => {
        navigate('/login') // üëà Redirigir al login
      }, 1500)
    } catch (error) {
      console.error(error)
      setMensaje(error.response?.data?.mensaje || 'Error al registrar')
    }
  }

  return (
    <Container maxWidth='sm' sx={{ mt: 6 }}>
      <Typography variant='h4' gutterBottom>
        Crear Cuenta üìù
      </Typography>

      <form onSubmit={handleSubmit}>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField label='Nombre de usuario' name='username' onChange={handleChange} required />
          <TextField label='Nombre completo' name='name' onChange={handleChange} required />
          <TextField label='Correo electr√≥nico' type='email' name='email' onChange={handleChange} required />
          <TextField label='Contrase√±a' type='password' name='password' onChange={handleChange} required />
          <Button type='submit' variant='contained'>Registrarse</Button>
        </Box>
      </form>

      {mensaje && (
        <Typography sx={{ mt: 2 }} color={mensaje.startsWith('‚úÖ') ? 'green' : 'error'}>
          {mensaje}
        </Typography>
      )}
    </Container>
  )
}
