import React, { useState } from 'react'
import { Box, Typography, Button, TextField, Paper, CircularProgress } from '@mui/material'

export default function PreguntaDinamica () {
  const [tema, setTema] = useState('')
  const [pregunta, setPregunta] = useState('')
  const [loading, setLoading] = useState(false)

  const generarPregunta = async () => {
    setLoading(true)
    try {
      const response = await fetch('http://localhost:3001/api/preguntas', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tema })
      })
      const data = await response.json()
      setPregunta(data.pregunta || 'No se generó ninguna pregunta.')
    } catch (error) {
      console.error('Error:', error)
      setPregunta('Hubo un error al generar la pregunta.')
    } finally {
      setLoading(false)
    }
  }

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #001F3F, #003366)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2
      }}
    >
      <Paper elevation={6} sx={{
        p: 5,
        borderRadius: 4,
        maxWidth: 600,
        width: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.05)',
        backdropFilter: 'blur(10px)',
        boxShadow: '0 8px 30px rgba(0,0,0,0.2)',
        color: 'white',
        textAlign: 'center'
      }}>
        <Typography variant="h4" fontWeight="bold" sx={{ color: '#FFD700', mb: 2 }}>
          Generador de Preguntas
        </Typography>

        <TextField
          label="Tema"
          variant="outlined"
          fullWidth
          value={tema}
          onChange={(e) => setTema(e.target.value)}
          sx={{
            input: { color: 'white' },
            label: { color: '#bbb' },
            mb: 3,
            '& .MuiOutlinedInput-root': {
              '& fieldset': { borderColor: '#FFD700' },
              '&:hover fieldset': { borderColor: '#FFA500' },
              '&.Mui-focused fieldset': { borderColor: '#FFD700' }
            }
          }}
        />

        <Button
          onClick={generarPregunta}
          variant="contained"
          sx={{
            backgroundColor: '#FFD700',
            color: '#003366',
            fontWeight: 'bold',
            '&:hover': { backgroundColor: '#FFC107' }
          }}
        >
          {loading ? <CircularProgress size={24} color="inherit" /> : 'Generar'}
        </Button>

        {pregunta && (
          <Typography variant="h6" mt={4} sx={{ color: '#FFF' }}>
            {pregunta}
          </Typography>
        )}
      </Paper>
    </Box>
  )
}
