import React from 'react'
import { Container, Typography, Button, Box, Paper } from '@mui/material'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'

export default function Home () {
  const navigate = useNavigate()

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2
      }}
    >
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, ease: 'easeOut' }}
      >
        <Paper
          elevation={12}
          sx={{
            p: 5,
            borderRadius: 4,
            backgroundColor: 'rgba(255, 255, 255, 0.05)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            color: 'white',
            maxWidth: 600,
            textAlign: 'center'
          }}
        >
          <Typography
            variant='h3'
            gutterBottom
            sx={{
              fontWeight: 700,
              textShadow: '0 0 15px rgba(0,0,0,0.3)'
            }}
          >
            ‚ú® AutoEval
          </Typography>

          <Typography variant='h6' sx={{ mb: 4, color: '#ccc' }}>
            Aprende con inteligencia. Progresa con prop√≥sito.  
            La IA adaptativa est√° aqu√≠ para ayudarte a brillar. üí°
          </Typography>

          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
            <Button
              variant='contained'
              size='large'
              onClick={() => navigate('/register')}
              sx={{
                background: 'linear-gradient(45deg, #00c6ff, #0072ff)',
                fontWeight: 'bold',
                color: '#fff',
                px: 4,
                '&:hover': {
                  background: 'linear-gradient(45deg, #00aaff, #0055cc)'
                }
              }}
            >
              Crear Cuenta
            </Button>

            <Button
              variant='outlined'
              size='large'
              onClick={() => navigate('/login')}
              sx={{
                borderColor: '#00c6ff',
                color: '#00c6ff',
                fontWeight: 'bold',
                px: 4,
                '&:hover': {
                  borderColor: '#fff',
                  backgroundColor: 'rgba(255,255,255,0.05)'
                }
              }}
            >
              Iniciar Sesi√≥n
            </Button>
          </Box>
        </Paper>
      </motion.div>
    </Box>
  )
}
